import React, { useState, useEffect, useRef } from 'react';
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  RefreshControl,
  Dimensions,
  Animated,
  StatusBar,
  Platform
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import TravelCard from '../components/TravelCard';
import { getTravels } from '../api/travelService';

const { width } = Dimensions.get('window');
const COLUMN_WIDTH = width / 2 - 16;

const HomeScreen = ({ navigation }) => {
  const [travels, setTravels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [loadingMore, setLoadingMore] = useState(false);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [keyword, setKeyword] = useState('');
  const [searchVisible, setSearchVisible] = useState(false);
  const [showScrollTop, setShowScrollTop] = useState(false);
  
  const flatListRef = useRef(null);
  const scrollY = useRef(new Animated.Value(0)).current;

  // 加载游记数据
  const loadTravels = async (pageNum = 1, searchKeyword = '') => {
    try {
      if (pageNum === 1) {
        setLoading(true);
      }
      
      const response = await getTravels(pageNum, 10, searchKeyword);
      
      if (pageNum === 1) {
        setTravels(response.data);
      } else {
        setTravels(prevTravels => [...prevTravels, ...response.data]);
      }
      
      setHasMore(response.page < response.pages);
      setPage(pageNum);
    } catch (error) {
      console.error('加载游记失败:', error);
    } finally {
      setLoading(false);
      setRefreshing(false);
      setLoadingMore(false);
    }
  };

  // 首次加载
  useEffect(() => {
    loadTravels();
  }, []);

  // 下拉刷新
  const handleRefresh = () => {
    setRefreshing(true);
    loadTravels(1, keyword);
  };

  // 加载更多
  const handleLoadMore = () => {
    if (loadingMore || !hasMore) return;
    setLoadingMore(true);
    loadTravels(page + 1, keyword);
  };

  // 搜索游记
  const handleSearch = () => {
    loadTravels(1, keyword);
    setSearchVisible(false);
  };

  // 跳转到我的游记页面
  const goToMyTravels = () => {
    navigation.navigate('MyTravels');
  };

  // 回到顶部
  const scrollToTop = () => {
    if (flatListRef.current) {
      flatListRef.current.scrollToOffset({ offset: 0, animated: true });
    }
  };

  // 处理滚动事件
  const handleScroll = Animated.event(
    [{ nativeEvent: { contentOffset: { y: scrollY } } }],
    {
      useNativeDriver: false,
      listener: event => {
        const offsetY = event.nativeEvent.contentOffset.y;
        setShowScrollTop(offsetY > 300);
      }
    }
  );

  // 渲染游记卡片
  const renderTravelItem = ({ item, index }) => (
    <View style={[
      styles.cardContainer,
      { marginLeft: index % 2 === 0 ? 0 : 8, marginRight: index % 2 === 0 ? 8 : 0 }
    ]}>
      <TravelCard travel={item} cardWidth={COLUMN_WIDTH} />
    </View>
  );

  // 渲染列表头部（搜索框）
  const renderHeader = () => (
    <View style={styles.header}>
      {searchVisible ? (
        <View style={styles.searchInputContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="搜索游记或作者"
            placeholderTextColor="#aaa"
            value={keyword}
            onChangeText={setKeyword}
            autoFocus
            returnKeyType="search"
            onSubmitEditing={handleSearch}
          />
          <TouchableOpacity
            style={styles.searchButton}
            onPress={handleSearch}
          >
            <Ionicons name="search" size={22} color="#fff" />
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.cancelButton}
            onPress={() => setSearchVisible(false)}
          >
            <Text style={styles.cancelButtonText}>取消</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <View style={styles.headerContent}>
          <TouchableOpacity
            style={styles.searchBar}
            onPress={() => setSearchVisible(true)}
          >
            <Ionicons name="search" size={20} color="#666" />
            <Text style={styles.searchPlaceholder}>搜索游记或作者</Text>
          </TouchableOpacity>
          
          <TouchableOpacity
            style={styles.myButton}
            onPress={goToMyTravels}
          >
            <Ionicons name="person" size={24} color="#1DA1F2" />
          </TouchableOpacity>
        </View>
      )}
    </View>
  );

  // 渲染底部加载器
  const renderFooter = () => {
    if (!loadingMore) return null;
    
    return (
      <View style={styles.footerLoader}>
        <ActivityIndicator size="small" color="#1DA1F2" />
        <Text style={styles.footerText}>正在加载更多...</Text>
      </View>
    );
  };

  // 渲染空状态
  const renderEmpty = () => {
    if (loading) return null;
    
    return (
      <View style={styles.emptyContainer}>
        <Ionicons name="images-outline" size={60} color="#ddd" />
        <Text style={styles.emptyText}>
          {keyword ? `没有找到与"${keyword}"相关的游记` : '暂无游记'}
        </Text>
        {keyword && (
          <TouchableOpacity
            style={styles.resetButton}
            onPress={() => {
              setKeyword('');
              loadTravels(1, '');
            }}
          >
            <Text style={styles.resetButtonText}>清除搜索</Text>
          </TouchableOpacity>
        )}
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" backgroundColor="#fff" />
      
      {renderHeader()}
      
      {loading && page === 1 ? (
        <View style={styles.loaderContainer}>
          <ActivityIndicator size="large" color="#1DA1F2" />
          <Text style={styles.loaderText}>加载中...</Text>
        </View>
      ) : (
        <FlatList
          ref={flatListRef}
          data={travels}
          renderItem={renderTravelItem}
          keyExtractor={(item) => item.id || item._id}
          numColumns={2}
          contentContainerStyle={styles.listContent}
          onEndReached={handleLoadMore}
          onEndReachedThreshold={0.3}
          refreshControl={
            <RefreshControl
              refreshing={refreshing}
              onRefresh={handleRefresh}
              colors={['#1DA1F2']}
              tintColor="#1DA1F2"
            />
          }
          ListFooterComponent={renderFooter}
          ListEmptyComponent={renderEmpty}
          onScroll={handleScroll}
          scrollEventThrottle={16}
        />
      )}
      
      {showScrollTop && (
        <TouchableOpacity
          style={styles.scrollTopButton}
          onPress={scrollToTop}
        >
          <Ionicons name="arrow-up" size={24} color="#fff" />
        </TouchableOpacity>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    backgroundColor: '#fff',
    paddingTop: Platform.OS === 'ios' ? 0 : 8,
    paddingBottom: 8,
    paddingHorizontal: 12,
    borderBottomWidth: 1,
    borderBottomColor: '#f0f0f0',
  },
  headerContent: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  searchBar: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#f5f8fa',
    borderRadius: 20,
    paddingHorizontal: 12,
    paddingVertical: 8,
    marginRight: 8,
  },
  searchPlaceholder: {
    marginLeft: 8,
    color: '#666',
    fontSize: 15,
  },
  myButton: {
    width: 40,
    height: 40,
    borderRadius: 20,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f8fa',
  },
  searchInputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  searchInput: {
    flex: 1,
    backgroundColor: '#f5f8fa',
    borderRadius: 20,
    paddingHorizontal: 12,
    paddingVertical: 8,
    fontSize: 15,
  },
  searchButton: {
    backgroundColor: '#1DA1F2',
    width: 36,
    height: 36,
    borderRadius: 18,
    justifyContent: 'center',
    alignItems: 'center',
    marginHorizontal: 8,
  },
  cancelButton: {
    paddingHorizontal: 8,
  },
  cancelButtonText: {
    color: '#1DA1F2',
    fontSize: 15,
  },
  listContent: {
    padding: 8,
    paddingBottom: 20,
  },
  cardContainer: {
    flex: 1,
    marginBottom: 16,
  },
  loaderContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loaderText: {
    marginTop: 10,
    fontSize: 15,
    color: '#666',
  },
  footerLoader: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    paddingVertical: 16,
