import React, { useState, useEffect, useRef } from 'react';
import {
  StyleSheet,
  View,
  Text,
  ScrollView,
  Image,
  TouchableOpacity,
  ActivityIndicator,
  Dimensions,
  Share,
  StatusBar,
  FlatList,
  Modal,
  Alert,
  Platform
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import MapView, { Marker } from 'react-native-maps';
import { Video } from 'expo-av';
import { getTravel } from '../api/travelService';
import { API_URL } from '../api/config';

const { width, height } = Dimensions.get('window');

const TravelDetailScreen = ({ route, navigation }) => {
  const { travelId } = route.params;
  const [travel, setTravel] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [imageViewerVisible, setImageViewerVisible] = useState(false);
  const [error, setError] = useState(null);
  
  const videoRef = useRef(null);
  const flatListRef = useRef(null);
  
  // 获取游记详情
  const loadTravelDetail = async () => {
    try {
      setLoading(true);
      const response = await getTravel(travelId);
      setTravel(response.data);
    } catch (error) {
      console.error('获取游记详情失败:', error);
      setError('获取游记详情失败，请重试');
    } finally {
      setLoading(false);
    }
  };
  
  // 初始化加载
  useEffect(() => {
    loadTravelDetail();
  }, [travelId]);
  
  // 处理图片滚动
  const handleImageScroll = (event) => {
    const slideIndex = Math.floor(
      event.nativeEvent.contentOffset.x / (width - 32)
    );
    setCurrentImageIndex(slideIndex);
  };
  
  // 分享游记
  const handleShare = async () => {
    if (!travel) return;
    
    try {
      const result = await Share.share({
        title: travel.title,
        message: `我在蓝书发现了一篇精彩的游记: ${travel.title}，快来看看吧！`,
        url: `https://your-app-domain.com/travels/${travel.id || travel._id}`
      });
      
      if (result.action === Share.sharedAction) {
        if (result.activityType) {
          console.log(`分享至: ${result.activityType}`);
        } else {
          console.log('分享成功');
        }
      } else if (result.action === Share.dismissedAction) {
        console.log('分享已取消');
      }
    } catch (error) {
      Alert.alert('分享失败', error.message);
    }
  };
  
  // 打开地图导航
  const handleOpenNavigation = () => {
    // 这里应该有游记定位信息，示例使用北京坐标
    const latitude = 39.9042;
    const longitude = 116.4074;
    const label = travel?.title || '游记位置';
    
    const scheme = Platform.select({
      ios: 'maps:',
      android: 'geo:'
    });
    const latLng = `${latitude},${longitude}`;
    const url = Platform.select({
      ios: `${scheme}?q=${label}&ll=${latLng}`,
      android: `${scheme}${latLng}?q=${label}`
    });
    
    Linking.openURL(url);
  };
  
  // 查看大图
  const openImageViewer = (index) => {
    setCurrentImageIndex(index);
    setImageViewerVisible(true);
  };
  
  // 渲染图片项
  const renderImageItem = ({ item, index }) => (
    <TouchableOpacity
      style={styles.imageItem}
      onPress={() => openImageViewer(index)}
      activeOpacity={0.9}
    >
      <Image
        source={{ uri: `${API_URL}/${item}` }}
        style={styles.slideImage}
        resizeMode="cover"
      />
    </TouchableOpacity>
  );
  
  // 渲染图片指示器
  const renderImageIndicator = () => {
    if (!travel || !travel.images || travel.images.length <= 1) return null;
    
    return (
      <View style={styles.indicatorContainer}>
        {travel.images.map((_, index) => (
          <View
            key={index}
            style={[
              styles.indicator,
              index === currentImageIndex && styles.activeIndicator
            ]}
          />
        ))}
      </View>
    );
  };
  
  // 渲染地图
  const renderMap = () => {
    // 示例使用北京坐标，实际应从游记数据中获取
    const latitude = 39.9042;
    const longitude = 116.4074;
    
    return (
      <View style={styles.mapContainer}>
        <Text style={styles.sectionTitle}>位置信息</Text>
        <MapView
          style={styles.map}
          initialRegion={{
            latitude,
            longitude,
            latitudeDelta: 0.02,
            longitudeDelta: 0.02,
          }}
        >
          <Marker
            coordinate={{ latitude, longitude }}
            title={travel?.title}
            description={travel?.author?.nickname || '游客'}
          />
        </MapView>
        
        <View style={styles.mapButtonsContainer}>
          <TouchableOpacity
            style={styles.mapButton}
            onPress={handleOpenNavigation}
          >
            <Ionicons name="navigate" size={18} color="#1DA1F2" />
            <Text style={styles.mapButtonText}>导航到此处</Text>
          </TouchableOpacity>
          
          <TouchableOpacity
            style={styles.mapButton}
            onPress={() => {
              // 这里可以实现查看周边兴趣点功能
              Alert.alert('提示', '查看周边功能尚未实现');
            }}
          >
            <Ionicons name="search" size={18} color="#1DA1F2" />
            <Text style={styles.mapButtonText}>周边兴趣点</Text>
          </TouchableOpacity>
        </View>
      </View>
    );
  };
  
  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#1DA1F2" />
        <Text style={styles.loadingText}>加载中...</Text>
      </View>
    );
  }
  
  if (error) {
    return (
      <View style={styles.errorContainer}>
        <Ionicons name="alert-circle-outline" size={60} color="#FF3B30" />
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity
          style={styles.retryButton}
          onPress={loadTravelDetail}
        >
          <Text style={styles.retryButtonText}>重试</Text>
        </TouchableOpacity>
      </View>
    );
  }
  
  if (!travel) {
    return (
      <View style={styles.errorContainer}>
        <Ionicons name="document-outline" size={60} color="#ddd" />
        <Text style={styles.errorText}>未找到游记</Text>
        <TouchableOpacity
          style={styles.retryButton}
          onPress={() => navigation.goBack()}
        >
          <Text style={styles.retryButtonText}>返回</Text>
        </TouchableOpacity>
      </View>
    );
  }
  
  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" backgroundColor="#fff" />
      
      <ScrollView contentContainerStyle={styles.content}>
        <Text style={styles.title}>{travel.title}</Text>
        
        <View style={styles.authorContainer}>
          <Image
            source={
              travel.author?.avatar
                ? { uri: `${API_URL}/${travel.author.avatar}` }
                : require('../assets/images/default-avatar.png')
            }
            style={styles.avatar}
          />
          
          <View style={styles.authorInfo}>
            <Text style={styles.authorName}>
              {travel.author?.nickname || '游客'}
            </Text>
            <Text style={styles.date}>
              {new Date(travel.createdAt).toLocaleDateString('zh-CN')}
            </Text>
          </View>
        </View>
        
        {travel.images && travel.images.length > 0 && (
          <View style={styles.imageSliderContainer}>
            <FlatList
              ref={flatListRef}
              data={travel.images}
              horizontal
              pagingEnabled
              showsHorizontalScrollIndicator={false}
              renderItem={renderImageItem}
              keyExtractor={(item, index) => `image-${index}`}
              onScroll={handleImageScroll}
              scrollEventThrottle={16}
              contentContainerStyle={styles.imageSlider}
            />
            {renderImageIndicator()}
          </View>
        )}
        
        {travel.video && (
          <View style={styles.videoContainer}>
            <Video
              ref={videoRef}
              source={{ uri: `${API_URL}/${travel.video}` }}
              style={styles.video}
              useNativeControls
              resizeMode="contain"
              isLooping={false}
              posterSource={{ uri: travel.images?.[0] ? `${API_URL}/${travel.images[0]}` : null }}
              posterStyle={{ resizeMode: 'cover' }}
            />
          </View>
        )}
        
        <Text style={styles.content}>{travel.content}</Text>
        
        {renderMap()}
        
        <View style={styles.actionContainer}>
          <TouchableOpacity
            style={styles.actionButton}
            onPress={handleShare}
          >
            <Ionicons name="share-social-outline" size={24} color="#1DA1F2" />
            <Text style={styles.actionButtonText}>分享</Text>
          </TouchableOpacity>
          
          <TouchableOpacity
            style={styles.actionButton}
            onPress={() => navigation.goBack()}
          >
            <Ionicons name="arrow-back-outline" size={24} color="#1DA1F2" />
            <Text style={styles.actionButtonText}>返回</Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
      
      <Modal
        visible={imageViewerVisible}
        transparent={true}
        onRequestClose={() => setImageViewerVisible(false)}
      >
        <View style={styles.imageViewerContainer}>
          <TouchableOpacity
            style={styles.closeButton}
            onPress={() => setImageViewerVisible(false)}
          >
            <Ionicons name="close" size={28} color="#fff" />
          </TouchableOpacity>
          
          <Image
            source={{ uri: `${API_URL}/${travel.images?.[currentImageIndex]}` }}
            style={styles.fullImage}
            resizeMode="contain"
          />
          
          <View style={styles.imageViewerIndicator}>
            <Text style={styles.imageViewerText}>
              {currentImageIndex + 1} / {travel.images?.length}
            </Text>
          </View>
        </View>
      </Modal>
    </View>
  );
};

