import React, { useState, useEffect } from 'react';
import {
  StyleSheet,
  View,
  Text,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  RefreshControl,
  Alert,
  StatusBar
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useIsFocused } from '@react-navigation/native';
import TravelCard from '../components/TravelCard';
import { getMyTravels, deleteTravel } from '../api/travelService';

const MyTravelsScreen = ({ navigation }) => {
  const [travels, setTravels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  
  const isFocused = useIsFocused();

  // 加载我的游记
  const loadMyTravels = async (pageNum = 1) => {
    try {
      if (pageNum === 1) {
        setLoading(true);
      }
      
      const response = await getMyTravels(pageNum, 10);
      
      if (pageNum === 1) {
        setTravels(response.data);
      } else {
        setTravels(prevTravels => [...prevTravels, ...response.data]);
      }
      
      setHasMore(response.page < response.pages);
      setPage(pageNum);
    } catch (error) {
      console.error('加载我的游记失败:', error);
      Alert.alert('提示', '加载游记失败，请重试');
    } finally {
      setLoading(false);
      setRefreshing(false);
      setLoadingMore(false);
    }
  };

  // 页面聚焦时重新加载数据
  useEffect(() => {
    if (isFocused) {
      loadMyTravels();
    }
  }, [isFocused]);

  // 下拉刷新
  const handleRefresh = () => {
    setRefreshing(true);
    loadMyTravels(1);
  };

  // 加载更多
  const handleLoadMore = () => {
    if (loadingMore || !hasMore) return;
    setLoadingMore(true);
    loadMyTravels(page + 1);
  };

  // 编辑游记
  const handleEdit = (travel) => {
    if (travel.status === 'approved') {
      Alert.alert('提示', '已通过审核的游记不能编辑');
      return;
    }
    
    navigation.navigate('EditTravel', { travelId: travel.id || travel._id });
  };

  // 删除游记
  const handleDelete = (travel) => {
    Alert.alert(
      '确认删除',
      '确定要删除这篇游记吗？此操作不可恢复。',
      [
        { text: '取消', style: 'cancel' },
        {
          text: '删除',
          style: 'destructive',
          onPress: async () => {
            try {
              setLoading(true);
              await deleteTravel(travel.id || travel._id);
              
              // 删除成功后刷新列表
              setTravels(prevTravels => 
                prevTravels.filter(item => (item.id || item._id) !== (travel.id || travel._id))
              );
              
              Alert.alert('提示', '游记已成功删除');
            } catch (error) {
              console.error('删除游记失败:', error);
              Alert.alert('提示', '删除游记失败，请重试');
            } finally {
              setLoading(false);
            }
          }
        }
      ]
    );
  };

  // 创建游记
  const handleCreate = () => {
    navigation.navigate('CreateTravel');
  };

  // 渲染游记项
  const renderTravelItem = ({ item }) => (
    <View style={styles.travelItem}>
      <TravelCard travel={item} />
      
      <View style={styles.actionContainer}>
        <View style={styles.statusContainer}>
          {item.status === 'pending' && (
            <View style={[styles.statusBadge, styles.pendingBadge]}>
              <Text style={styles.statusText}>审核中</Text>
            </View>
          )}
          
          {item.status === 'approved' && (
            <View style={[styles.statusBadge, styles.approvedBadge]}>
              <Text style={styles.statusText}>已通过</Text>
            </View>
          )}
          
          {item.status === 'rejected' && (
            <View style={styles.rejectContainer}>
              <View style={[styles.statusBadge, styles.rejectedBadge]}>
                <Text style={styles.statusText}>未通过</Text>
              </View>
              {item.rejectReason && (
                <Text style={styles.rejectReason}>
                  原因: {item.rejectReason}
                </Text>
              )}
            </View>
          )}
        </View>
        
        <View style={styles.buttons}>
          <TouchableOpacity
            style={[
              styles.actionButton,
              item.status === 'approved' && styles.disabledButton
            ]}
            onPress={() => handleEdit(item)}
            disabled={item.status === 'approved'}
          >
            <Ionicons
              name="create-outline"
              size={20}
              color={item.status === 'approved' ? '#aaa' : '#1DA1F2'}
            />
            <Text
              style={[
                styles.actionButtonText,
                item.status === 'approved' && styles.disabledButtonText
              ]}
            >
              编辑
            </Text>
          </TouchableOpacity>
          
          <TouchableOpacity
            style={styles.actionButton}
            onPress={() => handleDelete(item)}
          >
            <Ionicons name="trash-outline" size={20} color="#FF3B30" />
            <Text style={[styles.actionButtonText, { color: '#FF3B30' }]}>
              删除
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );

  // 渲染空状态
  const renderEmpty = () => {
    if (loading) return null;
    
    return (
      <View style={styles.emptyContainer}>
        <Ionicons name="document-text-outline" size={60} color="#ddd" />
        <Text style={styles.emptyText}>您还没有发布任何游记</Text>
        <TouchableOpacity
          style={styles.createButton}
          onPress={handleCreate}
        >
          <Text style={styles.createButtonText}>立即发布游记</Text>
        </TouchableOpacity>
      </View>
    );
  };

  // 渲染底部加载器
  const renderFooter = () => {
    if (!loadingMore) return null;
    
    return (
      <View style={styles.footerLoader}>
        <ActivityIndicator size="small" color="#1DA1F2" />
        <Text style={styles.footerText}>正在加载更多...</Text>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" backgroundColor="#fff" />
      
      {loading && !refreshing && !loadingMore ? (
        <View style={styles.loaderContainer}>
          <ActivityIndicator size="large" color="#1DA1F2" />
          <Text style={styles.loaderText}>加载中...</Text>
        </View>
      ) : (
        <FlatList
          data={travels}
          renderItem={renderTravelItem}
          keyExtractor={(item) => item.id || item._id}
          contentContainerStyle={styles.listContent}
          onEndReached={handleLoadMore}
          onEndReachedThreshold={0.3}
          refreshControl={
            <RefreshControl
              refreshing={refreshing}
              onRefresh={handleRefresh}
              colors={['#1DA1F2']}
              tintColor="#1DA1F2"
            />
          }
          ListEmptyComponent={renderEmpty}
          ListFooterComponent={renderFooter}
        />
      )}
      
      <TouchableOpacity
        style={styles.floatingButton}
        onPress={handleCreate}
      >
        <Ionicons name="add" size={30} color="#fff" />
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  listContent: {
    padding: 16,
    paddingBottom: 80,
  },
  travelItem: {
    marginBottom: 20,
    backgroundColor: '#fff',
    borderRadius: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 2,
    overflow: 'hidden',
  },
  actionContainer: {
    paddingHorizontal: 12,
    paddingVertical: 8,
    borderTopWidth: 1,
    borderTopColor: '#f0f0f0',
  },
  statusContainer: {
    marginBottom: 8,
  },
  statusBadge: {
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 4,
    alignSelf: 'flex-start',
